import React, { Component } from "react"

import Passo from "./Passo"
import Display from "./Display"
import Buttons from "./Buttons"

export default class MyContador extends Component {

    state = {
        passo: this.props.passo || 1,
        valor: this.props.valor || 0
    }

    getPasso = (val) => {
        this.setState({ passo: val })
    }

    getStt = (simbolo) => {
        this.setState({ valor: `${simbolo}${this.state.passo}` })
    }

    render() {
        return (
            <div>
                <h2>Contador{this.state.valor}</h2>
                <Passo passo={this.state.passo} pegarPasso={this.getPasso}></Passo>
                <Display valor={this.state.valor}></Display>
                <Buttons passo={this.state.passo} pagarStt={this.getStt}></Buttons>
            </div>
        )
    }
}
