import React from "react"

export default props => {
    function passarValor(valor) {
        props.pegarPasso()
    }

    return (
        <div>
            <label htmlFor="passoInput"></label>
            <input type="number" value={props.passoValor}
              onChange={e => passarValor(+e.target.value)}/>
        </div>
    )
}
