import React, { Component } from "react"

import Botao from "./Botao"
import Exibir from "./Exibir"

export default class MegaSena extends Component {

    state = {
        gerar: false,
        numeros: []
    }

    numAleatorio = () => {
        Math.floor(Math.random() * 59 + 1)
    }

    gerarNumeros = () => {
        if8()
        for(let i = 0; i <= 5; i++) {
            let num = this.numAleatorio

            if(this.state.numeros.indexOf(num)) {
                return
            } else {
                this.state.numeros.fill(num)
            }
        }
    }

    render() {
        return (
            <div>
                <h2>Mega-Sena</h2>

                <Botao gerar={this.gerarNumeros}></Botao>
                <Exibir></Exibir>
            </div>
        )
    }
}
