import React, { Component } from "react"

import Botao from "./Botao"
import Exibir from "./Exibir"

export default class MegaSena extends Component {

    state = {
        numeros: []
    }

    gerarNumeros = () => {
        for(let i = 1; this.state.numeros.length !== 6; i++) {
            let num = Number(Math.floor(Math.random() * 2 + 1))
            console.log(num)
            
            if(!this.state.numeros.includes(num)) {
                this.setState({
                    numeros: this.state.numeros.push(num)
                })
                console.log(this.state.numeros)
                console.log("Tamanho: " + this.state.numeros.length)
            }
        }
    }

    render() {
        return (
            <div>
                <h2>Mega-Sena</h2>

                <Botao gerar={this.gerarNumeros}></Botao>
                <Exibir></Exibir>
            </div>
        )
    }
}
