import React from "react"

export default props => {

    function inc() {
        props.pegarValor(props.valor + props.passo)
    }

    function dec() {
        props.pegarValor(props.valor - props.passo)
    }

    return (
        <div>
            <button onClick={inc}>+</button>
            <button onClick={dec}>-</button>
        </div>
    )
}

