import React from "react"

export default props => {

    function inc() {
        props.pegarValor = props.valor + this.props.passo
    }

    function dec() {
        this.props.pegarValor = this.props.valor - this.props.passo
    }

    return (
        <div>
            <button onClick={inc()}>+</button>
            <button onClick={dec()}>-</button>
        </div>
    )
}
